const form=document.querySelector("form"),cityInput=document.getElementById("city");form.addEventListener("submit",t=>{t.preventDefault();t=cityInput.value;fetch(`http://api.openweathermap.org/data/2.5/weather?q=${t}&units=metric&APPID=5d066958a60d315387d9492393935c19`).then(t=>t.json()).then(t=>{var e=t.main.temp,n=t.main.pressure,d=t.weather[0].description,m=t.main.humidity,o=t.wind.speed,r=t.wind.deg,t=t.weather[0].icon;document.getElementById("temp").textContent=e,document.getElementById("pressure").textContent=n,document.getElementById("description").textContent=d,document.getElementById("humidity").textContent=m,document.getElementById("speed").textContent=o,document.getElementById("deg").textContent=r,document.getElementById("icon").setAttribute("src",`http://openweathermap.org/img/w/${t}.png`)}).catch(t=>alert(t+" Enter corect city. Try another city"))});